<!--
 * iview icon支持自定义，此组件暂不更新
-->
<template>
  <i :class='classes' :style="styles" @click="handleClick"></i>
</template>

<script>
const prefixCls = 'brIcon';
const iconCls = 'iconfont';
// const iconPre = 'br-icon';

export default {
  name: 'BrIcon',
  props: {
    type: {
      type: String,
      default: ''
    },
    htmlClass: {
      type: String,
      default: ''
    },
    size: [Number, String],
    color: String,
    custom: {
      type: String,
      default: ''
    }
  },
  computed: {
    classes () {
      return [
        `${prefixCls}`,
        {
          [`${iconCls} ${this.type}`]: this.type !== '',
          [`${this.custom}`]: this.custom !== '',
        },
        `${this.htmlClass}`
      ];
    },
    styles () {
      let style = {};
      if (this.size) {
        style['font-size'] = `${this.size}px`;
      }
      if (this.color) {
        style.color = this.color;
      }
      return style;
    }
  },
  methods: {
    handleClick (event) {
      this.$emit('click', event);
    }
  }
};
</script>

<style lang="less">
@import '../../assets/icons/iconfont.css';

.brIcon {
  display: inline-block;
  speak: none;
  font-style: normal;
  font-weight: 400;
  font-variant: normal;
  text-transform: none;
  text-rendering: auto;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  vertical-align: middle;
}
.iconfont {
  font-size: inherit;
}
</style>
