<template>
  <transition name="move-up" @enter="handleEnter" @leave="handleLeave">
    <div class="br-message" :style="styles">
      <div :class="BRMclass">
        <template>
          <div style="display:inline-block;" v-if="type === 'loading'">
            <Spin>
                <Icon :type="typeMap" :class="loadingIconClass"></Icon>
            </Spin>
          </div>
          <Icon v-else :type="typeMap" :class="iconClass"></Icon>
        </template>
        <slot>
          <p v-show="message" class="br-message__content">{{ message }}</p>
          <div v-show="render" class="br-message__content">
            <RenderDom :render="renderFunc"></RenderDom>
          </div>
        </slot>
        <Icon type="ios-close-empty" class="br-message-close" v-show="closable" @click="close"></Icon>
      </div>
    </div>
  </transition>
</template>

<script>
import RenderDom from '../../base/components/render';

const iconTypes = {
  'info': 'information-circled',
  'success': 'checkmark-circled',
  'warning': 'android-alert',
  'error': 'close-circled',
  'loading': 'load-c'
};

export default {
  name: 'BrMessage',
  components: {
    RenderDom
  },
  props: {
    name: {
      type: String,
      required: true
    },
    type: {
      type: String,
      default: 'info'
    },
    closable: {
      type: Boolean,
      default: false
    },
    render: {
      type: Function
    },
    onClose: {
      type: Function
    },
    message: {
      type: String
    },
    duration: {
      type: Number,
      default: 3000
    },
    styles: {
      type: Object,
      default: function () {
        return {
          right: '50%'
        };
      }
    }
  },
  data () {
    return {
      timer: null
    };
  },
  computed: {
    renderFunc () {
      return this.render || function () {};
    },
    BRMclass () {
      return [
        'br-message-body',
        this.type ? 'br-message-' + this.type : ''
      ];
    },
    loadingIconClass () {
      return [
        'br-message-icon',
        'spin-icon-load',
        this.type ? 'br-message-icon-' + this.type : ''
      ];
    },
    iconClass () {
      return [
        'br-message-icon',
        this.type ? 'br-message-icon-' + this.type : ''
      ];
    },
    typeMap () {
      return iconTypes[this.type];
    }
  },
  mounted () {
    this.init();
  },
  methods: {
    init () {
      if (this.duration !== 0) {
        this.timer = setTimeout(() => {
          this.close();
        }, this.duration);
      }
    },
    clearTimer () {
      if (this.timer) {
        clearTimeout(this.timer);
        this.timer = null;
      }
    },
    close () {
      this.clearTimer();
      this.onClose && this.onClose();
      this.$parent.close(this.name);
    },
    handleEnter (el) {
      el.style.height = el.scrollHeight + 'px';
    },
    handleLeave (el) {
      if (document.getElementsByClassName('br-message').length !== 1) {
        el.style.height = 0;
        el.style.paddingTop = 0;
        el.style.paddingBottom = 0;
      }
    }
  }
};
</script>

<style lang="less" scoped>
@import '../../../styles/var.less';
@import '../../base/style/animation.less';

.br-message {
  padding: 8px 0;
  text-align: center;
  transition: height .3s ease-in-out,padding .3s ease-in-out;
  .br-message-body {
    display: inline-block;
    padding: 8px 16px;
    border-radius: 4px;
    box-shadow: 0 1px 6px rgba(0, 0, 0, .2);
    background: #fff;
    pointer-events: all;
    .br-message-icon {
      font-size: 1.2em;
    }
    .br-message__content {
      display: inline-block;
      margin: 0 16px;
    }
    .br-message-close {
      font-size: 16px;
      cursor: pointer;
      &:hover {
        color: @br-default-color;
      }
    }
  }
}
.br-message-info, .br-message-loading {
  border: 2px solid @br-default-color;
  .br-message-icon-info, .br-message-icon-loading {
    color: @br-default-color;
  }
}

.br-message-success{
  border: 2px solid @br-success-color;
  .br-message-icon-success {
    color: @br-success-color;
  }
}

.br-message-warning {
  border: 2px solid @br-warn-color;
  .br-message-icon-warning {
    color: @br-warn-color;
  }
}

.br-message-error {
  border: 2px solid @br-error-color;
  .br-message-icon-error {
    color: @br-error-color;
  }
}
.spin-icon-load {
  animation: ani-demo-spin 1s linear infinite;
}
@keyframes ani-demo-spin {
  from { transform: rotate(0deg); }
  50% { transform: rotate(180deg); }
  to { transform: rotate(360deg); }
}
</style>

