<template>
  <div class="check-box-all">
    <Checkbox
      :indeterminate="indeterminate"
      :value="checkAll"
      @click.prevent.native="handleCheckAll">全选</Checkbox>
    <CheckboxGroup v-model="checkAllGroup" @on-change="checkAllGroupChange">
      <Checkbox v-for="(item, index) in data" :key="index" :label="item.value">{{item.label}}</Checkbox>
    </CheckboxGroup>
  </div>
</template>
<script>
export default {
  name: 'CheckBoxAll',
  props: {
    data: [Array],
    value: [Array]
  },
  data () {
    return {
      indeterminate: true,
      checkAll: false,
      checkAllGroup: []
    };
  },
  watch: {
    value () {
      this.init();
    }
  },
  mounted () {
    this.init();
  },
  methods: {
    init () {
      const arr = this.value || [];
      this.checkAllGroup = [...arr];
      this.checkAllchange(arr);
    },
    handleCheckAll () {
      if (this.indeterminate) {
        this.checkAll = false;
      } else {
        this.checkAll = !this.checkAll;
      }
      this.indeterminate = false;

      if (this.checkAll) {
        this.checkAllGroup = this.data.map(item => item.value);
      } else {
        this.checkAllGroup = [];
      }
      this.checkAllGroupChange(this.checkAllGroup);
    },
    checkAllchange (selects) {
      if (selects.length === this.data.length) {
        this.indeterminate = false;
        this.checkAll = true;
      } else if (selects.length > 0) {
        this.indeterminate = true;
        this.checkAll = false;
      } else {
        this.indeterminate = false;
        this.checkAll = false;
      }
    },
    checkAllGroupChange (selects) {
      this.$emit('input', selects);
      this.$emit('on-change', selects);
      this.checkAllchange(selects);
    }
  }
};
</script>

<style lang="less" scoped>
.check-box-all {
  .ivu-checkbox-group {
    display: inline-block;
  }
}
</style>
