<template>
  <span class="nav-icon">
    <template v-show="data.icon">
      <BrIcon :custom="data.icon" v-if="data.isBrIcon && data.isCustom"></BrIcon>
      <BrIcon :type="data.icon" v-else-if="data.isBrIcon && !data.isCustom"></BrIcon>
      <Icon :type="data.icon" v-else></Icon>
    </template>
  </span>
</template>

<script>
import BrIcon from '../../BrIcon';

export default {
  name: 'NavIcon',
  components: {BrIcon},
  props: {
    data: {
      type: Object,
      default () {
        return {};
      }
    }
  }
};
</script>
