<template>
  <div class="nav-box">
    <template v-if="layout == 'left'">
      <div :class="navThemeClass" >
        <Menu
          v-show="!isCollapsed"
          mode='vertical'
          width="auto"
          ref="sideMenu"
          :class="menuitemClasses"
          :theme="theme"
          :active-name="activeName"
          :open-names="openNames"
          @on-select="handleMenuClick"
          >
          <ExpandMenu
            v-for="(item, i) in navs"
            :key="'expand' + i"
            :data="item"
            :children-key="childrenKey" />
        </Menu>
        <ShrinkMenu
          v-show="isCollapsed"
          v-for="(item, i) in navs"
          :data="item"
          :key="'shrink' + i"
          :theme="theme"
          :children-key="childrenKey"
          @on-select="handleMenuClick"/>
      </div>
    </template>
    <template v-else>
      <div :class="navThemeClass" >
        <ul class="nav-menu-level clear-float">
          <LevelMenu
            v-for="(item, i) in navs"
            :key="'level' + i"
            :data="item"
            :theme="theme"
            :children-key="childrenKey"
            @on-select="handleMenuClick" />
        </ul>
      </div>
    </template>
  </div>
</template>

<script>

/**
 * {
 *    title: '',
      name: '', // router name
      url: '/products', // 全路由,进行导航
      icon: 'cube',
      isBrIcon: false, // 是否采用BrIcon
      isCustom: false //自定义icon
 * }
 */

import ExpandMenu from './components/ExpandMenu';
import ShrinkMenu from './components/ShrinkMenu';
import LevelMenu from './components/LevelMenu';

export default {
  name: 'NavMenu',
  data: function () {
    return {
      navs: [],
      childrenKey: 'children',
      activeName: '',
      openNames: []
    };
  },
  components: {
    ExpandMenu,
    ShrinkMenu,
    LevelMenu
  },
  props: {
    data: {
      type: Array,
      required: true,
      default: () => []
    },
    isCollapsed: {
      type: Boolean,
      default: false
    },
    layout: {
      type: String,
      default: 'left'
    },
    theme: {
      type: String,
      default: 'dark'
    },
    opens: {
      type: Array,
      default: () => []
    },
    active: String
  },
  watch: {
    // $route () {
    //   this.setMenuName();
    // },
    data () {
      this.navs = [...this.data];
    },
    opens (newOpens) {
      this.openNames = [...newOpens];
    },
    active (newActive) {
      this.activeName = newActive;
    }
  },
  created () {
    this.navs = [...this.data];
    // this.setMenuName();
  },
  mounted () {
    this.openNames = [...this.opens];
    this.activeName = this.active;
  },
  computed: {
    navThemeClass () {
      return [
        'nav-theme',
        'nav-theme-' + this.theme
      ];
    },
    menuitemClasses () {
      return [
        'nav-expand-menu',
        this.isCollapsed ? 'collapsed-menu' : ''
      ];
    }
  },
  methods: {
    // setMenuName () {
    //   const {matched} = this.$route;
    //   // console.log(this.$route.matched);
    //   const openName = matched[1].name;

    //   this.activeName = matched[2] ? matched[2].name : '';
    //   this.openNames = [openName];
    //   // console.log(this.activeName, this.openNames);
    // },
    handleMenuClick (name) {
      if (name.indexOf('/') < 0) {
        this.$router.push({name});
      } else {
        this.$router.push(name);
      }
    }
  },
  updated () {
    this.$nextTick(() => {
      if (this.$refs.sideMenu) {
        //  console.log(this.activeName, this.openNames);
        this.$refs.sideMenu.updateOpened();
        this.$refs.sideMenu.updateActiveName();
      }
    });
  }
};
</script>

<style lang="less">
  @import './index.less';
</style>
