<style lang="less">
  @import '../styles/menu.less';
</style>

<template>
  <Menu ref="sideMenu" :active-name="activeName" :open-names="openNames" :theme="menuTheme" width="auto" accordion @on-select="changeMenu">
    <template v-for="(item, index) in menuList">
      <MenuItem v-if="item.children.length < 1" :name="item.path " :key="index">
        <Icon :type="item.icon" :size="iconSize" :key="'icon'+item.path" class="icon-valign"></Icon>
        <span class="layout-text" :key="item.path">{{ itemTitle(item) }}</span>
      </MenuItem>

      <Submenu v-if="item.children.length >= 1" :name="item.path" :key="item.path">
        <template slot="title">
          <Icon :type="item.icon" :size="iconSize" class="icon-valign"></Icon>
          <span class="layout-text">{{ itemTitle(item) }}</span>
        </template>
        <template v-for="(child, idx) in item.children">
          <MenuItem :name="child.path" :key="child.path">
            <Icon :type="child.icon" :size="iconSize" :key="idx" v-if="showSubIcon"></Icon>
            <span class="layout-text" :key="child.path">{{ child.title }}</span>
          </MenuItem>
        </template>
      </Submenu>
    </template>
  </Menu>
</template>

<script>
  export default {
    name: 'sidebarMenu',
    props: {
      menuList: Array,
      iconSize: {
        Number,
        default: 18
      },
      showSubIcon: {
        type: Boolean,
        default: false
      },
      subIconSize: {
        Number,
        default: 18
      },
      menuTheme: {
        type: String,
        default: 'dark'
      },
      openNames: {
        type: Array
      },
      activeName: {
        type: String
      }
    },
    methods: {
      changeMenu(active) {
        this.$emit('on-change', active);
      },
      itemTitle(item) {
        if (typeof item.title === 'object') {
          return this.$t(item.title.i18n);
        } else {
          return item.title;
        }
      }
    },
    updated() {
      this.$nextTick(() => {
        if (this.$refs.sideMenu) {
          this.$refs.sideMenu.updateOpened();
          this.$refs.sideMenu.updateActiveName();
        }
      });
    }

  };
</script>
<style lang="less" scoped>
  .icon-valign {
    vertical-align: text-bottom;
  }
</style>
