<style lang="less">
  @import './styles/menu.less';
</style>

<template>
  <div :style="{background: bgColor}" class="ivu-shrinkable-menu">
    <slot name="top"></slot>
    <sidebar-menu
      v-show="!shrink"
      :icon-size="iconSize"
      :sub-icon-size="subIconSize"
      :show-sub-icon="showSubIcon"
      :menu-theme="theme"
      :menu-list="menuList"
      :open-names="openNames"
      :active-name="activeName"
      @on-change="handleChange"
    ></sidebar-menu>
    <sidebar-menu-shrink
      v-show="shrink"
      :menu-theme="theme"
      :menu-list="menuList"
      :icon-color="shrinkIconColor"
      @on-change="handleChange"
    ></sidebar-menu-shrink>
  </div>
</template>

<script>
import sidebarMenu from './components/sidebarMenu.vue';
import sidebarMenuShrink from './components/sidebarMenuShrink.vue';
export default {
  name: 'shrinkableMenu',
  components: {
    sidebarMenu,
    sidebarMenuShrink
  },
  props: {
    shrink: {
      type: Boolean,
      default: false
    },
    menuList: {
      type: Array,
      required: true
    },
    iconSize: {
      Number,
      default: 18
    },
    showSubIcon: {
      type: Boolean,
      default: false
    },
    subIconSize: {
      Number,
      default: 18
    },
    theme: {
      type: String,
      default: 'dark'
    },
    beforePush: {
      type: Function
    },
    openNames: {
      type: Array
    },
    activeName: {
      type: String
    }
  },
  computed: {
    bgColor () {
      return this.theme === 'dark' ? '#081225' : '#F7F7F7';
    },
    shrinkIconColor () {
      return this.theme === 'dark' ? '#fff' : '#081225';
    }
  },
  methods: {
    handleChange (name) {
      let willpush = true;
      if (this.beforePush !== undefined) {
        if (!this.beforePush(name)) {
          willpush = false;
        }
      }
      if (willpush) {
        this.$router.push({
          path: name
          // name: name
        });
      }
      this.$emit('on-change', name);
    }
  }
};
</script>
