    </MenuItem>
  </Menu>
</template>

<script>
export default {
  name: 'topMenu',
  props: {
    toolbarList: {
      type: Array
    },
    activeName: {
      type: String
    }
  },
  data () {
    return {
    };
  },
  components: {
  },
  methods: {
    handleChangeTopMenu (id) {
      this.$emit('changeTopMenu', id);
    }
  }
};
</script>

