import  Vue from 'vue';

const app = {
  state: {
    menuTheme: 'dark', // 主题
    themeColor: ''
  },
  mutations: {
    changeMenuTheme (state, theme) {
      state.menuTheme = theme;
    },
    changeMainTheme (state, mainTheme) {
      state.themeColor = mainTheme;
    }
  }
};

export default app;
